import { observable, action } from "mobx";
import GET from "../services/GET";

class WordStore {
  @observable words = [];
  @observable flag = true;

  @action async getWordServer() {
    const wordServer = await GET.getWord();
    this.words = wordServer;
  }

  @action setWords = (newWords) => {
    this.words = newWords;
  };

  @action toggleFlag = () => {
    this.flag = !this.flag;
  };

  @action updateWord(id, updatedData) {
    const index = this.words.findIndex((word) => word.id === id);
    if (index !== -1) {
      this.words[index] = updatedData;
    }
  }
}

const wordStore = new WordStore();

export default wordStore;
