        <div className={style.col}>Russian</div>
        <div className={style.col}>Edit</div>
        </div>
        {words.map ((item, index)=> (
        <TableItem 
        key = {index}
        {...item} flag={flag} setFlag={setFlag}/>))}</div>
        </div>
        
    )
}

export default Table;
