import { observarble, action } from "mobx";
import { observer } from "mobx-react";
import React, { useEffect } from "react";
import GET from "../services/GET";

class WordStore {
  @observarble words = false;
  @observarble flag = true;

  @action async getWordServer() {
    const wordServer = await GET.getWord();
    this.words = wordServer;
  }

  @action toggleFlag() {
    this.flag = !this.flag;
  }

  wordStore = new WordStore();

  DataMobX = observer(({ children }) => {
    useEffect(() => {
      this.wordStore.getWordServer();
    }, [this.wordStoreflag]);

    if (!this.wordStore.words) return <h1>Loading...</h1>;

    return children;
  });
}

export default DataMobX;

export { WordStore };

